package com.nit.core;
//odd and even using if else block 
public class IfElse {
    static int a=203;
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		if(a%2==0) {
			System.out.println("number is even");
		}
		else {
			System.out.println("number is odd");
		}

	}

}
